import { useState, useEffect } from &#39;react&#39;; function useFetch(url) { const [data, setData] = useState(null); const [loading, setLoading] = useState(true); const [error, setError] = useState(null); useEffect(() =&gt; { async function fetchData() { try { setLoading(true); const response = await fetch(url); const json = await response.json(); setData(json); } catch (err) { setError(err); } finally { setLoading(false); } } fetchData(); }, [url]); return { data, loading, error }; } // Usage function UserProfile({ userId }) { const { data, loading, error } = useFetch(`/api/users/${userId}`); if (loading) return &lt;div&gt;Loading...&lt;/div&gt;; if (error) return &lt;div&gt;Error: {error.message}&lt;/div&gt;; return &lt;div&gt;{data.name}&lt;/div&gt;; } 2. useLocalStorage Hook L∆∞u v√† ƒë·ªçc data t·ª´ localStorage:
function useLocalStorage(key, initialValue) { const [storedValue, setStoredValue] = useState(() =&gt; { try { const item = window.localStorage.getItem(key); return item ? JSON.parse(item) : initialValue; } catch (error) { return initialValue; } }); const setValue = (value) =&gt; { try { setStoredValue(value); window.localStorage.setItem(key, JSON.stringify(value)); } catch (error) { console.error(error); } }; return [storedValue, setValue]; } // Usage function Settings() { const [theme, setTheme] = useLocalStorage(&#39;theme&#39;, &#39;light&#39;); return ( &lt;select value={theme} onChange={(e) =&gt; setTheme(e.target.value)}&gt; &lt;option value=&#34;light&#34;&gt;Light&lt;/option&gt; &lt;option value=&#34;dark&#34;&gt;Dark&lt;/option&gt; &lt;/select&gt; ); } 3. useDebounce Hook Debounce gi√° tr·ªã ƒë·ªÉ tr√°nh qu√° nhi·ªÅu re-renders:
import { useState, useEffect } from &#39;react&#39;; function useDebounce(value, delay) { const [debouncedValue, setDebouncedValue] = useState(value); useEffect(() =&gt; { const handler = setTimeout(() =&gt; { setDebouncedValue(value); }, delay); return () =&gt; { clearTimeout(handler); }; }, [value, delay]); return debouncedValue; } // Usage function SearchBox() { const [searchTerm, setSearchTerm] = useState(&#39;&#39;); const debouncedSearchTerm = useDebounce(searchTerm, 500); useEffect(() =&gt; { if (debouncedSearchTerm) { // Perform search performSearch(debouncedSearchTerm); } }, [debouncedSearchTerm]); return ( &lt;input value={searchTerm} onChange={(e) =&gt; setSearchTerm(e.target.value)} placeholder=&#34;Search...&#34; /&gt; ); } 4. useClickOutside Hook Detect click outside element:
import { useEffect, useRef } from &#39;react&#39;; function useClickOutside(callback) { const ref = useRef(); useEffect(() =&gt; { function handleClickOutside(event) { if (ref.current &amp;&amp; !ref.current.contains(event.target)) { callback(); } } document.addEventListener(&#39;mousedown&#39;, handleClickOutside); return () =&gt; { document.removeEventListener(&#39;mousedown&#39;, handleClickOutside); }; }, [callback]); return ref; } // Usage function Dropdown() { const [isOpen, setIsOpen] = useState(false); const ref = useClickOutside(() =&gt; setIsOpen(false)); return ( &lt;div ref={ref}&gt; &lt;button onClick={() =&gt; setIsOpen(!isOpen)}&gt;Toggle&lt;/button&gt; {isOpen &amp;&amp; &lt;div&gt;Dropdown content&lt;/div&gt;} &lt;/div&gt; ); } Best Practices 1. Naming Convention Lu√¥n b·∫Øt ƒë·∫ßu v·ªõi &ldquo;use&rdquo;:
